var HomepageContainer = React.createClass({displayName: "HomepageContainer",
  getInitialState: function() {
    return {
      loadingSteps: 0,
      classHidden: "",
      classEffect: "",
      width: 0,
      height: 0
    };
  },

  componentWillMount: function() {
    var ticker = window.setInterval(function() {
      var diff = Math.random() * 10;

      this.setState({
        loadingSteps: this.state.loadingSteps + diff
      });

      if (this.state.loadingSteps > 100) {
        window.clearInterval(ticker);
      };
    }.bind(this), 100);
  },

  progressFinish: function() {
    setTimeout(function() {
      this.setState({
        loadingSteps: 100,
        classEffect: 'animated fadeOut'
      });
    }.bind(this), 2500);

    setTimeout(function() {
      this.setState({
        classHidden: 'hidden'
      });
    }.bind(this), 3500);
  },

  render: function() {
    return (
      React.createElement("div", {className: "homepage-content"}, 

      React.createElement(Preloader, {
        completed: this.state.loadingSteps, 
        color: "#333", 
        height: "2", 
        classEffect: this.state.classEffect, 
        classHidden: this.state.classHidden}
      ), 

      React.createElement(HomepageVideo, {onLoadedFinish: this.progressFinish}), 

      React.createElement(HomepageGrid, null)

      )
    );
  }
});

