var Preloader = React.createClass({displayName: "Preloader",
  render: function() {
    var completed = +this.props.completed;
    if (completed < 0) {completed = 0};
    if (completed > 100) {completed = 100};

    var style = {
      backgroundColor: this.props.color || '#0BD318',
      width: completed + '%',
      transition: "width 200ms",
      height: this.props.height || 10
    };

    var preloaderClass = "preloader-wrapper " + this.props.classHidden,
        panelClass = "preloader-bottomPanel " + this.props.classEffect;


    return (
      React.createElement("div", {className: preloaderClass}, 

          React.createElement("div", {id: "preloader-topPanel", className: panelClass}), 

          React.createElement("div", {id: "preloader-bottomPanel", className: panelClass}, 
            React.createElement("div", {className: "preloader-title text-center"}, 
              "please Hang On while we are prepping some stuff...", 
              React.createElement("br", null)
            ), 

            React.createElement("div", {className: "progressbar-container"}, 
              React.createElement("div", {className: "progressbar-progress", style: style}, this.props.children)
            )
          )
      )
    );
  }
});

